import React from "react";
import { BrowserRouter as Route, Link, Routes } from "react-router-dom";

// Header Component
function Header() {
  return (
    <header>
      <img src="/logo.png" alt="Application Logo" />
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/register">Register</Link>
          </li>
          <li>
            <Link to="/login">Login</Link>
          </li>
          <li>
            <Link to="/contact">Contact</Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

function MainSection() {
  return (
    <main>
      <h1>Welcome to the Alumni Network!</h1>
      <p>
        This network helps you stay connected with fellow alumni and leverage
        professional opportunities.
      </p>
      <div>
        <img src="/alumni-event.jpg" alt="Alumni Event" />
        {/* Additional images or a slider can be added here */}
      </div>
      <div>
        <button onClick={() => (window.location.href = "/register")}>
          Register Now
        </button>
        <button onClick={() => (window.location.href = "/login")}>Login</button>
      </div>
    </main>
  );
}

// Footer Component
function Footer() {
  return (
    <footer>
      <p>
        Contact us at:{" "}
        <a href="mailto:contact@alumni.net">contact@alumni.net</a>
      </p>
      <p>Follow us on social media</p>
      {/* Social media links */}
      <ul>
        <li>
          <a href="#">Privacy Policy</a>
        </li>
        <li>
          <a href="#">Terms of Use</a>
        </li>
      </ul>
    </footer>
  );
}

function App() {
  return (

<div> <Header />
      <Route path="/" exact component={MainSection} />
      {/* Additional routes for About, Register, Login, Contact */}
      <Footer />
      </div>
  );
}

export default App;
