import React from "react";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import styles from "./App.css";
import RegistrationForm from "./RegistrationForm/RegistrationForm";

function Header() {
  return (
    <header>
      <img src="/fdiba-logo.png" alt="FDIBA Logo" className="logo" />
      <h3>FDIBA Alumni</h3>
      <nav>
        <ul>
          <li>
            <Link to="/">Home</Link>
          </li>
          <li>
            <Link to="/about">About</Link>
          </li>
          <li>
            <Link to="/contact">Contact</Link>
          </li>
        </ul>
      </nav>
    </header>
  );
}

function HomePage() {
  return (
    <main>
      <h1>Welcome to the Alumni Network!</h1>
      <p>
        This network helps you stay connected with fellow alumni and leverage
        professional opportunities.
      </p>
      <div>
        <img src="/alumni-event.jpg" alt="Alumni Event" />
      </div>
      <div style={styles.buttons}>
        <button onClick={() => (window.location.href = "/register")}>
          Register Now
        </button>
        <button onClick={() => (window.location.href = "/login")}>Login</button>
      </div>
    </main>
  );
}

function Footer() {
  return (
    <footer>
      <p>
        Contact us at:{" "}
        <a href="mailto:contact@alumni.net">contact@alumni.net</a>
      </p>
      <p>Follow us on social media</p>
    </footer>
  );
}

function App() {
  return (
    <Router>
      <Header />
      <Routes>
        <Route path="/" element={<HomePage />} />
        <Route path="/register" element={<RegistrationForm />} />
        <Route path="/login" element={<div>Login Form Here</div>} />
        <Route path="/about" element={<div>About Us</div>} />
        <Route path="/contact" element={<div>Contact Information</div>} />
      </Routes>
      <Footer />
    </Router>
  );
}

export default App;
